import {useEffect , useState, useCallback} from 'react';


function Profile(props: any){
    const[items , setitems] = useState<any>(props)
    const[shouldusershow, setshouldusershow] = useState<boolean>(false)
    useEffect(() => {
        if(props !== {}){
            setitems(props.data?.items)
        }
      }, [props,showitem]);


      function showitem(){
        if(items !== {}){
          setshouldusershow(true)
        }
    }

        return(
            <div>
                {shouldusershow &&<img src={items[0]?.profile_image} alt="profile_image" ></img>}
                <button onClick={()=> console.log(items[0])}>xxxxx</button>
            </div>
        )
}

export default Profile;
